def RequestId = jsonRequest.Id def RequestVersion = jsonRequest.Version def RequestSeqId = jsonRequest.Margins[0].SeqId def RequestRoomsSeqId = jsonRequest.Margins[0].Rooms[0].SeqId
def ResponseId = jsonResponse.Id def ResponseVersion = jsonResponse.Version def ResponseSeqId = jsonResponse.Margins[0].SeqId def ResponseRoomsSeqId = jsonResponse.Margins[0].Rooms[0].SeqId
assert (ResponseId.toString().equalsIgnoreCase(RequestId)) : "Response and Request ID fields should match (ignoring case) Request= "+RequestId+" Response= "+ResponseId assert (ResponseVersion.toString() == RequestVersion) : "Response and Request Version fields should match. Request= "+RequestVersion + " Response= "+ResponseVersion assert (ResponseSeqId.toString() == RequestSeqId) : "Response and Request SeqId fields should match. Request= "+RequestSeqId + " Response= "+ResponseSeqId assert (ResponseRoomsSeqId.toString() == RequestRoomsSeqId) : "Response and Request RoomsSeqId fields should match. Request= "+RequestRoomsSeqId + " Response= "+ResponseRoomsSeqId

