if(currentRow <totalRecords){ 
	currentRow=currentRow+1 
	context.setProperty("currentRow", currentRow) 
	testRunner.gotoStepByName("SetProperties") 
	log.info" loop back for data row " + currentRow

	} 
	else 
	{ 
	log.info "Completed" 
	}

