def firstRunOfGet 	= RootResultFolder + "/" + currentRow + "_Initial Get Method Run_Flight ID_" 	+ 	"ID" + Id  + "OutboundFlight" + OutboundFlightId  + "_" + time + ".txt"

if(testCount == 0) {
    outputFile(firstRunOfGet)Margin_GetMargins - Request 1
} else {
    outputFile(fileName)
}
